package com.github.jactivityindicator;

import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.InputStream;

import javax.imageio.ImageIO;
import javax.imageio.ImageReader;
import javax.swing.ImageIcon;
import javax.swing.JLabel;

import com.sun.imageio.plugins.gif.GIFImageReader;
import com.sun.imageio.plugins.gif.GIFImageReaderSpi;

/**
 * Java Activity Indicator for Swing
 * 
 * @author wolf.posdorfer
 * 
 */
public class JActivityIndicator extends JLabel
{
    private static final long serialVersionUID = 5845264027228385562L;

    public static final int SMALL_GREY = 0;
    public static final int CIRCLE_LIGHT_GREY = 1;

    private final String smallgrey = "";

    private final String lightgreycircle = "";

    private final ImageIcon _iconRotating;

    private final ImageIcon _iconStill;

    private boolean _isRotating = false;

    /**
     * Creates a new JActivityIndicator using a predefined Style
     * 
     * @param style
     *            valid styles are {@link #SMALL_GREY} or
     *            {@link #CIRCLE_LIGHT_GREY}
     */
    public JActivityIndicator(int style)
    {
        byte[] bytes = null;
        if (style == SMALL_GREY)
        {
            bytes = decodeString(smallgrey);
        }
        else if (style == CIRCLE_LIGHT_GREY)
        {
            bytes = decodeString(lightgreycircle);
        }
        else
        {
            throw new IllegalArgumentException("PROVIDE VALID STYLE");
        }

        _iconRotating = new ImageIcon(bytes);
        _iconStill = makeStillImage(new ByteArrayInputStream(bytes));

        setIcon(_iconStill);
    }

    /**
     * Creates a new JActivityIndicator from a given file
     * 
     * @param filename
     *            file pointing to image
     * 
     */
    public JActivityIndicator(File filename)
    {
        _iconRotating = new ImageIcon(filename.getAbsolutePath());
        ImageIcon img;
        try
        {
            img = makeStillImage(new FileInputStream(filename));
        }
        catch (FileNotFoundException e)
        {
            img = new ImageIcon();
        }
        _iconStill = img;
    }

    /**
     * Creates a new JActivityIndicator from a byte-Array
     * 
     * @param byteArr
     *            byteArray containing the Imageinfos
     */
    public JActivityIndicator(byte[] byteArr)
    {
        _iconRotating = new ImageIcon(byteArr);
        _iconStill = makeStillImage(new ByteArrayInputStream(byteArr));
    }

    /**
     * Creates a new JActivityIndicator from a Hexadecimal-byte-Array-String
     * format
     * 
     * @param builder
     */
    public JActivityIndicator(CharSequence builder)
    {
        byte[] bytes = decodeString(builder.toString());
        _iconRotating = new ImageIcon(bytes);
        _iconStill = makeStillImage(new ByteArrayInputStream(bytes));
    }

    private ImageIcon makeStillImage(InputStream stream)
    {
        ImageIcon result = null;
        try
        {
            ImageReader ir = new GIFImageReader(new GIFImageReaderSpi());
            ir.setInput(ImageIO.createImageInputStream(stream));
            result = new ImageIcon(ir.read(0));
        }
        catch (Exception e)
        {
            result = new ImageIcon();
        }
        return result;
    }

    /**
     * Decodes a String representing an Image as byteArray in Hexadecimal format
     * 
     * @param imagestring
     *            String containing image bytearray
     * @return byte-Array
     */
    public byte[] decodeString(String imagestring)
    {
        byte[] result = new byte[imagestring.length() / 2];
        if (imagestring.length() % 2 == 0)
        {
            int i = 0;
            while (i < imagestring.length())
            {
                byte b = (byte) ((Character.digit(imagestring.charAt(i), 16) << 4) + Character.digit(
                        imagestring.charAt(i + 1), 16));
                result[i / 2] = b;
                i += 2;
            }
        }
        return result;
    }

    /**
     * Starts the rotation
     */
    public void startRotating()
    {
        if (!_isRotating)
        {
            _isRotating = true;
            setIcon(_iconRotating);
        }
    }

    /**
     * Stops the rotation
     */
    public void stopRotating()
    {
        if (_isRotating)
        {
            _isRotating = false;
            setIcon(_iconStill);
        }
    }

    /**
     * Toggles the rotation.<br>
     * If rotating, stops rotating else starts rotating
     */
    public void toggleRotating()
    {
        if (_isRotating)
        {
            stopRotating();
        }
        else
        {
            startRotating();
        }
    }

    /**
     * Is the JActivityIndicator currently rotating
     * 
     * @return <code>true</code> if rotating, else <code>false</code>
     */
    public boolean isRotating()
    {
        return _isRotating;
    }
}
